mapper.update:
update tb_reply
set reply = #{reply}
, updateDate = CURDATE()
where seq_rno = #{seq_rno}

mapper.select:


	SELECT *
	FROM
	(
		SELECT
			@rn := @rn + 1 AS RN , A.* 
		FROM tb_reply AS A USE INDEX (idx_reply)
		CROSS JOIN (SELECT @rn := 0 ) AS R 
		WHERE A.SEQ_BNO = #{seq_bno}
	) AS R
	WHERE RN > ( #{cri.pageNum} - 1) * #{cri.amount}
	LIMIT #{cri.amount}
